var myChart1 = echarts.init(document.getElementById('echarts-shouru'));
myChart1.showLoading();    //数据加载完之前先显示一段简单的loading动画
var names=[];    //类别数组（实际用来盛放X轴坐标值）
var dd=[];    //销量数组（实际用来盛放Y坐标值）
var je=[];
$.ajax({
type : "post",
async : true,            //异步请求（同步请求将会锁住浏览器，用户其他操作必须等待请求完成才可以执行）
url : url+"/Orders/ajax",    //请求发送到TestServlet处
data : {},
dataType : "json",        //返回数据形式为json
success : function(result) {
 //请求成功时执行该函数内容，result即为服务器返回的json对象
 if (result) {
        for(var i=0;i<result.length;i++){       
           names.push(result[i].m);    //取出ts的月份
         }
        for(var i=0;i<result.length;i++){       
            dd.push(result[i].dd);    //挨个取出ts量
          }
        for(var i=0;i<result.length;i++){       
            je.push(result[i].je);    //挨个取出wz量
          }
        myChart1.hideLoading();    //隐藏加载动画
        myChart1.setOption({ 
            title: {
                 text: '本年订单量和营业额'
             },
             tooltip: {},
             legend: {
                 data:["订单量", "营业额"]
             },
             xAxis: {
                 data: names
             },
             yAxis: {},       //加载数据图表
            series: [{
                name: "订单量",
                type: "bar",
                data: dd ,
                markPoint: {
                    data: [{
                        type: "max",
                        name: "最大值"
                    },
                    {
                        type: "min",
                        name: "最小值"
                    }]
                }                          
            },
            {
                name: "营业额",
                type: "bar",
                data: je ,
                markLine: {
                data: [{
                    type: "average",
                    name: "平均值"
                }]
                }                           
            }]
        });
 }
 
},
 error : function(errorMsg) {
     //请求失败时执行该函数
 alert("图表请求数据失败!");
 myChart1.hideLoading();
 }
});
